import React, { useState } from 'react';
import './Category.css';
import Modal from './Modal'; // Import the Modal component

const CategoryCarousel = ({ title, category }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [modalData, setModalData] = useState({ isOpen: false, title: '', description: '' });
  const [enrolledItems, setEnrolledItems] = useState([]); // Store enrolled item indexes

  const goToNext = () => {
    if (currentIndex + 4 < category.length) {
      setCurrentIndex(currentIndex + 4);
    }
  };

  const goToPrev = () => {
    if (currentIndex - 4 >= 0) {
      setCurrentIndex(currentIndex - 4);
    }
  };

  const openModal = (item) => {
    setModalData({
      isOpen: true,
      title: item.title,
      description: item.description,
    });
  };

  const closeModal = () => {
    setModalData({ isOpen: false, title: '', description: '' });
  };

  const handleEnroll = (index) => {
    if (!enrolledItems.includes(index)) {
      setEnrolledItems([...enrolledItems, index]); // Add this item to enrolled list
    }
  };

  const renderCategoryItems = (items) => {
    return items.map((item, index) => (
      <div className="category-item" key={index}>
        {/* Click image to open modal (drill-down) */}
        <img
          src={item.image}
          alt={item.title}
          className="category-image"
          onClick={() => openModal(item)}
        />
        <h4>{item.title}</h4>
        <p>{item.description}</p>
        {/* Enroll Button */}
        <button className="enroll-btn" onClick={() => handleEnroll(currentIndex + index)}>
          Enroll Now
        </button>
        {/* Show "Enrolled successfully" if enrolled */}
        {enrolledItems.includes(currentIndex + index) && (
          <p className="enrolled-message">Enrolled successfully!</p>
        )}
      </div>
    ));
  };

  return (
    <div className="category-section">
      <h3 className="category-title">{title}</h3>
      <div className="category-carousel">
        <button className="carousel-btn prev-btn" onClick={goToPrev}>
          ◄
        </button>
        <div className="carousel-content">
          <div className="category-row">
            {renderCategoryItems(category.slice(currentIndex, currentIndex + 4))}
          </div>
        </div>
        <button className="carousel-btn next-btn" onClick={goToNext}>
          ►
        </button>
      </div>

      {/* Modal for displaying item details (drill-down) */}
      <Modal
        isOpen={modalData.isOpen}
        onClose={closeModal}
        title={modalData.title}
        description={modalData.description}
      />
    </div>
  );
};

export default CategoryCarousel;
