import React, { useState, useEffect } from 'react';
import './Profile.css';
import YourProfile from './YourProfile'; // Import YourProfile component

const Profile = () => {
    const [menuOpen, setMenuOpen] = useState(false);
    const [selectedMenu, setSelectedMenu] = useState('Your Profile'); // Set default to 'Your Profile'

    const toggleMenu = () => {
        setMenuOpen(!menuOpen); // Toggle between true/false to open/close the menu
    };

    const handleMenuClick = (menu) => {
        setSelectedMenu(menu); // Set the selected menu item
    };

    useEffect(() => {
        // This ensures that 'Your Profile' is selected initially when the component mounts
        setSelectedMenu('Your Profile');
    }, []);

    // Debugging log for selected menu
    useEffect(() => {
        console.log(selectedMenu);
    }, [selectedMenu]);

    return (
        <div className="profile-page">
            {/* Sidebar */}
            <div className={`sidebar ${menuOpen ? 'open' : ''}`}>
                <button className="toggle-btn" onClick={toggleMenu}>
                    {menuOpen ? '←' : '→'}
                </button>
                <ul>
                    <li onClick={() => handleMenuClick('Your Profile')}>Your Profile</li>
                    <li onClick={() => handleMenuClick('My Programs')}>My Programs</li>
                    <li onClick={() => handleMenuClick('My Diet Plans')}>My Diet Plans</li>
                    <li onClick={() => handleMenuClick('My Articles')}>My Articles</li>
                    <li onClick={() => handleMenuClick('My Medical History')}>My Medical History</li>
                    <li onClick={() => handleMenuClick('My Progress')}>My Progress</li>
                    <li onClick={() => handleMenuClick('My Trainer')}>My Trainer</li>
                    <li onClick={() => handleMenuClick('My Exercise')}>My Trainer</li>
                    <Link to="/exercise-tracking">Exercise Tracking</Link>
                    <li onClick={() => handleMenuClick('My Subscription')}>My Subscription</li>
                </ul>
            </div>

            {/* Main Profile Content */}
            <div className="profile-content">
                <div className="profile-header">
                    <div className="profile-pic">
                        <img src="/p.jpg.jpg" alt="Profile Pic" />
                    </div>
                    <h2>Shreya Sharma</h2>
                </div>
                
                <div className="content">
                    {/* Conditionally render based on selected menu */}
                    {selectedMenu === 'Your Profile' && (
                        <YourProfile /> // Render the YourProfile component when 'Your Profile' is selected
                    )}
                    
                    {selectedMenu === 'My Programs' && (
                        <div>
                            {/* Add the "Currently Enrolled" heading only in "My Programs" */}
                            <h3 style={{ padding: '2px' , margin:'10px'}}>Currently Enrolled</h3>
                            <div className="programs">
                                <img src="/v1.png" alt="Program 1" className="program-img" />
                                <img src="/v2.jpg" alt="Program 2" className="program-img" />
                                <img src="/v3.jpg" alt="Program 3" className="program-img" />
                            </div>
                        </div>
                    )}

                    {/* Add similar conditional rendering for other menu options if needed */}
                </div>
            </div>
        </div>
    );
};

export default Profile;
