import React, { useState } from 'react';
import './MyMedicalHistory.css';

const MyMedicalHistory = () => {
  const [medicalHistory, setMedicalHistory] = useState({
    weight: '60',
    age: '25',
    bloodGroup: 'B+',
    healthIssues: 'None',
    description: 'No major health concerns at the moment.'
  });

  const [isEditing, setIsEditing] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setMedicalHistory({
      ...medicalHistory,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = () => {
    setIsEditing(false);
    setMessage('Medical history saved successfully!');
  };

  const handleEditClick = () => {
    setIsEditing(true);
    setMessage('');
  };

  const handleCloseClick = () => {
    setIsEditing(false);
  };

  return (
    <div className="medical-history-container">
      <h2>My Medical History</h2>

      <div className="medical-details">
        <div className="medical-item">
          <strong>Weight (kg):</strong>
          {isEditing ? (
            <input
              type="number"
              name="weight"
              value={medicalHistory.weight}
              onChange={handleChange}
            />
          ) : (
            <span>{medicalHistory.weight}</span>
          )}
        </div>

        <div className="medical-item">
          <strong>Age:</strong>
          {isEditing ? (
            <input
              type="number"
              name="age"
              value={medicalHistory.age}
              onChange={handleChange}
            />
          ) : (
            <span>{medicalHistory.age}</span>
          )}
        </div>

        <div className="medical-item">
          <strong>Blood Group:</strong>
          {isEditing ? (
            <input
              type="text"
              name="bloodGroup"
              value={medicalHistory.bloodGroup}
              onChange={handleChange}
            />
          ) : (
            <span>{medicalHistory.bloodGroup}</span>
          )}
        </div>

        <div className="medical-item">
          <strong>Health Issues:</strong>
          {isEditing ? (
            <input
              type="text"
              name="healthIssues"
              value={medicalHistory.healthIssues}
              onChange={handleChange}
            />
          ) : (
            <span>{medicalHistory.healthIssues}</span>
          )}
        </div>

        <div className="medical-item">
          <strong>Description:</strong>
          {isEditing ? (
            <textarea
              name="description"
              value={medicalHistory.description}
              onChange={handleChange}
            />
          ) : (
            <span>{medicalHistory.description}</span>
          )}
        </div>
      </div>

      {isEditing ? (
        <>
          <button onClick={handleSave} className="save-btn">
            Save Changes
          </button>
        </>
      ) : (
        <button onClick={handleEditClick} className="edit-btn">
          Click here to edit
        </button>
      )}

      {message && <p className="success-message">{message}</p>}
    </div>
  );
};

export default MyMedicalHistory;
