import React, { useState } from 'react'; 
import './Recipes.css';

const RecipesCarousel = ({ title, category }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [favorites, setFavorites] = useState([]); // Store added items in favorites

  const goToNext = () => {
    if (currentIndex + 4 < category.length) {
      setCurrentIndex(currentIndex + 4);
    }
  };

  const goToPrev = () => {
    if (currentIndex - 4 >= 0) {
      setCurrentIndex(currentIndex - 4);
    }
  };

  const handleAddToFavorites = (item) => {
    const addedDate = new Date().toLocaleDateString();
    setFavorites([...favorites, { item, date: addedDate }]);  // Add item to favorites with date
  };

  const renderCategoryItems = (items) => {
    return items.map((item, index) => (
      <div className="recipe-item" key={index}>
        <img src={item.image} alt={item.title} className="recipe-image" />
        <h4>{item.title}</h4>
        <p>{item.description}</p>
        <button className="favorite-btn" onClick={() => handleAddToFavorites(item)}>
          Add to Favorites
        </button>

        {/* Show message when the item is added to favorites */}
        {favorites.some(favorite => favorite.item.title === item.title) && (
          <p className="favorite-message">Added to Favorites Successfully.</p>
        )}
      </div>
    ));
  };

  return (
    <div className="recipe-section">
      <h3 className="recipe-title">{title}</h3>
      <div className="recipe-carousel">
        <button className="carousel-btn prev-btn" onClick={goToPrev}>
          ◄
        </button>
        <div className="carousel-content">
          <div className="recipe-row">
            {renderCategoryItems(category.slice(currentIndex, currentIndex + 4))}
          </div>
        </div>
        <button className="carousel-btn next-btn" onClick={goToNext}>
          ►
        </button>
      </div>
    </div>
  );
};

const Recipes = () => {
  const indianVegSalads = [
    { image: '/images/r1.jpeg', title: 'Cucumber Salad', description: 'A refreshing salad made with cucumber, tomatoes, and spices.' },
    { image: '/images/r2.jpg', title: 'Mixed Vegetable Salad', description: 'A vibrant mix of seasonal vegetables with lemon dressing.' },
    { image: '/images/r3.jpg', title: 'Sprouted Moong Salad', description: 'A nutritious salad made with sprouted moong beans.' },
    { image: '/images/r4.jpg', title: 'Carrot and Cucumber Salad', description: 'A crunchy and fresh salad with carrots and cucumbers.' },
    { image: '/images/r5.jpeg', title: 'Kachumber Salad', description: 'A simple yet flavorful salad made with onions, tomatoes, and cucumber.' },
    { image: '/images/r6.jpg', title: 'Chana Chaat', description: 'Chickpeas mixed with tomatoes, onions, and spices.' },
    { image: '/images/r7.jpeg', title: 'Pineapple Salad', description: 'A sweet and tangy salad made with pineapple and mint.' },
    { image: '/images/r8.jpg', title: 'Beetroot Salad', description: 'A healthy and colorful salad made with beetroots and carrots.' },
  ];

  const vegHighProteinMeals = [
    { image: '/images/m1.jpeg', title: 'Lentil Curry', description: 'A protein-packed curry made with lentils and spices.' },
    { image: '/images/m2.jpeg', title: 'Tofu Stir Fry', description: 'A stir-fry dish made with tofu and mixed vegetables.' },
    { image: '/images/m3.jpg', title: 'Chickpea Salad', description: 'A protein-rich salad made with chickpeas and fresh veggies.' },
    { image: '/images/m4.jpg', title: 'Paneer Tikka', description: 'Grilled paneer cubes with Indian spices.' },
    { image: '/images/m5.jpg', title: 'Quinoa Salad', description: 'A light and healthy salad made with quinoa and roasted veggies.' },
    { image: '/images/m6.jpg', title: 'Vegetable Biryani', description: 'A spiced rice dish loaded with vegetables and high in protein.' },
    { image: '/images/m7.jpg', title: 'Rajma', description: 'Kidney beans cooked in a savory tomato gravy rich in protein.' },
    { image: '/images/m8.jpeg', title: 'Moong Dal', description: 'A simple and high-protein dal made with moong beans.' },
  ];

  const vegHighCalciumMeals = [
    { image: '/images/i1.jpg', title: 'Palak Paneer', description: 'A creamy spinach curry with paneer, rich in calcium.' },
    { image: '/images/i2.png', title: 'Calcium-Rich Tofu Stir Fry', description: 'A stir fry dish made with tofu and leafy greens rich in calcium.' },
    { image: '/images/i3.jpeg', title: 'Methi Thepla', description: 'A Gujarati flatbread made with fenugreek leaves, rich in calcium.' },
    { image: '/images/i4.jpeg', title: 'Soybean Salad', description: 'A calcium-packed salad made with soybeans and vegetables.' },
    { image: '/images/i5.jpg', title: 'Dhokla', description: 'A steamed snack made with chickpea flour and rich in calcium.' },
    { image: '/images/i6.jpeg', title: 'Almond Milk Smoothie', description: 'A healthy smoothie made with almond milk and fruits.' },
    { image: '/images/i7.jpeg', title: 'Sesame Ladoo', description: 'Sweet balls made with sesame seeds, packed with calcium.' },
    { image: '/images/i8.jpg', title: 'Drumstick Curry', description: 'A tasty curry made with drumsticks, rich in calcium.' },
  ];

  const vegMineralsRichMeals = [
    { image: '/images/j1.jpeg', title: 'Kale and Spinach Salad', description: 'A very high mineral-rich salad made with kale and spinach.' },
    { image: '/images/j2.jpg', title: 'Broccoli Stir Fry', description: 'Well Stir-fried broccoli loaded with essential minerals.' },
    { image: '/images/j3.jpg', title: 'Sweet Potato Salad', description: 'A healthy and tasty salad with sweet potatoes rich in minerals.' },
    { image: '/images/j4.jpeg', title: 'Lentil Soup', description: 'A hearty and nutritious soup made with lentils rich in minerals.' },
    { image: '/images/j5.jpg', title: 'Chia Pudding', description: 'A healthy dessert made with chia seeds.' },
    { image: '/images/j6.jpg', title: 'Cabbage Stir Fry', description: 'Stir-fried cabbage with rich mineral content.' },
    { image: '/images/j7.jpeg', title: 'Carrot Soup', description: 'A simple and mineral-packed carrot soup.' },
    { image: '/images/j8.jpg', title: 'Cauliflower Rice', description: 'A healthy alternative to rice made from cauliflower.' },
  ];

  return (
    <div>
      <RecipesCarousel title="Indian Veg Salads" category={indianVegSalads} />
      <RecipesCarousel title="Veg High Protein Meal" category={vegHighProteinMeals} />
      <RecipesCarousel title="Veg High Calcium Meal" category={vegHighCalciumMeals} />
      <RecipesCarousel title="Veg Minerals Rich Meal" category={vegMineralsRichMeals} />
    </div>
  );
};

export default Recipes;
