import React, { useState } from "react";
import "./DataCollection.css";

const GEMINI_API_KEY = "AIzaSyCgz-p2aVMhAfPapKiuqpPk5fSU1mczYgI";

const DataCollection = () => {
  const [sources, setSources] = useState([""]);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleInputChange = (index, value) => {
    const updatedSources = [...sources];
    updatedSources[index] = value;
    setSources(updatedSources);
  };

  const addSource = () => {
    setSources([...sources, ""]);
  };

  const extractMainArticle = async (url) => {
    const response = await fetch(url);
    const html = await response.text();
    const tempDiv = document.createElement("div");
    tempDiv.innerHTML = html;

    const article =
      tempDiv.querySelector("article") ||
      tempDiv.querySelector('[class*="article"]') ||
      tempDiv.querySelector('[class*="content"]');

    const rawText = article ? article.innerText : tempDiv.innerText;

    const cleanText = rawText
      .split("\n")
      .filter(line =>
        line.trim().length > 40 &&
        !line.toLowerCase().includes("related articles") &&
        !line.toLowerCase().includes("also read")
      )
      .join("\n");

    return cleanText.slice(0, 7000);
  };

  const summarizeWithGemini = async (text) => {
    const prompt = `
You are a professional news risk analyst. Given the following news article, generate a structured report with:

- Headline (short title)
- Professional Summary (3-5 lines)
- Authenticity Level (Low, Medium, High)
- Urgency Level (Low, Medium, High)
- Impact Scope (e.g., local, state, national, international)
- Recommendation (action if any)

News Article:
"""${text}"""
`;

    const res = await fetch(
      `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GEMINI_API_KEY}`,
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          contents: [{ parts: [{ text: prompt }] }],
        }),
      }
    );

    const data = await res.json();
    return data.candidates?.[0]?.content?.parts?.[0]?.text || "No response";
  };

  const handleSelect = async (index) => {
    const selectedUrl = sources[index];

    if (!selectedUrl.startsWith("https://")) {
      alert("Please enter a valid URL!");
      return;
    }

    setLoading(true);
    try {
      const articleText = await extractMainArticle(selectedUrl);
      const result = await summarizeWithGemini(articleText);
      setAnalysis(result);
    } catch (error) {
      alert("Error during analysis: " + error.message);
    }
    setLoading(false);
  };

  return (
    <div className="data-collection">
      <h2 className="title">Early-Warning AI Dashboard</h2>
      <div className="sources-container">
        {sources.map((source, index) => (
          <div key={index} className="source-input">
            <input
              type="text"
              value={source}
              onChange={(e) => handleInputChange(index, e.target.value)}
              placeholder={`Enter News URL ${index + 1}`}
            />
            <button className="select-button" onClick={() => handleSelect(index)}>
              Analyze
            </button>
          </div>
        ))}
        <button className="add-source" onClick={addSource}>
          + Add New Source
        </button>
      </div>

      {loading && <p style={{ marginTop: "20px" }}>Analyzing with Gemini AI...</p>}

      {analysis && (
        <div className="analysis-result">
          <h3>ðŸ“Š Gemini Analysis</h3>
          <pre style={{ whiteSpace: "pre-wrap", background: "#f2f2f2", padding: "1rem", borderRadius: "6px" }}>
            {analysis}
          </pre>
        </div>
      )}
    </div>
  );
};

export default DataCollection;
