import React, { useState } from "react";

function AIAnalysis() {
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleAnalyze = () => {
    if (!url.trim()) {
      alert("Please enter a news URL.");
      return;
    }

    setLoading(true);
    fetch(`http://localhost/rag2/api/analyze.php?url=${encodeURIComponent(url)}`)
      .then((response) => response.json())
      .then((data) => {
        setAnalysis(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching analysis:", error);
        setLoading(false);
      });
  };

  return (
    <div className="analysis-container">
      <h1>AI News Analysis</h1>
      <input
        type="text"
        placeholder="Enter news URL..."
        value={url}
        onChange={(e) => setUrl(e.target.value)}
      />
      <button onClick={handleAnalyze}>Analyze</button>

      {loading && <p>Analyzing news...</p>}

      {analysis && (
        <div className="analysis-results">
          <h2>Analysis Results</h2>
          <p><strong>Title:</strong> {analysis.title}</p>
          <p><strong>Authenticity Score:</strong> {analysis.authenticity_score}</p>
          <p><strong>Sentiment:</strong> {analysis.sentiment}</p>
          <p><strong>Summary:</strong> {analysis.summary}</p>
          <p><strong>URL:</strong> <a href={analysis.url} target="_blank" rel="noopener noreferrer">{analysis.url}</a></p>
        </div>
      )}
    </div>
  );
}

export default AIAnalysis;
