import React, { useState, useEffect } from "react";
import "./AlertsReports.css";

const AlertsReports = () => {
  const [filter, setFilter] = useState("All");
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedDateFilter, setSelectedDateFilter] = useState("All Dates");
  const [specificDate, setSpecificDate] = useState("");
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAlerts = async () => {
      setLoading(true);
      try {
        const response = await fetch(
          `https://api.ambeedata.com/natural-disasters/latest`,
          {
            method: "GET",
            headers: {
              "x-api-key": "YOUR_API_KEY", // Replace with your Ambee API key
              "Content-type": "application/json",
            },
          }
        );
        const data = await response.json();
        setAlerts(data.data); // Adjust according to the API response structure
      } catch (error) {
        console.error("Error fetching alerts:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAlerts();
  }, []);

  const applyDateFilter = (alert) => {
    const alertDate = new Date(alert.date);
    const today = new Date();

    if (selectedDateFilter === "Last Week") {
      const lastWeek = new Date();
      lastWeek.setDate(today.getDate() - 7);
      return alertDate >= lastWeek;
    }

    if (selectedDateFilter === "Last Month") {
      const lastMonth = new Date();
      lastMonth.setDate(today.getDate() - 30);
      return alertDate >= lastMonth;
    }

    if (selectedDateFilter === "Specific Date" && specificDate) {
      return alert.date === specificDate;
    }

    return true; // If "All Dates" is selected, return all alerts
  };

  const filteredAlerts = alerts.filter(
    (alert) =>
      (selectedCountry === "" || alert.country === selectedCountry) &&
      (filter === "All" || alert.level === filter) &&
      applyDateFilter(alert)
  );

  return (
    <div className="alerts-reports-container">
      <h1 className="title">⚠️ Global Geopolitical Alerts & Reports</h1>

      {/* Filter Controls */}
      <div className="controls">
        <select
          className="filter-dropdown"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="All">All Levels</option>
          <option value="High">High Risk</option>
          <option value="Medium">Medium Risk</option>
          <option value="Low">Low Risk</option>
        </select>

        <select
          className="filter-dropdown"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">Select Country</option>
          {/* Populate this dropdown based on the countries available in your alerts data */}
        </select>

        {/* Date Filter */}
        <select
          className="filter-dropdown"
          value={selectedDateFilter}
          onChange={(e) => setSelectedDateFilter(e.target.value)}
        >
          <option value="All Dates">All Dates</option>
          <option value="Last Week">Last Week</option>
          <option value="Last Month">Last Month</option>
          <option value="Specific Date">Specific Date</option>
        </select>

        {selectedDateFilter === "Specific Date" && (
          <input
            type="date"
            className="date-picker"
            value={specificDate}
            onChange={(e) => setSpecificDate(e.target.value)}
          />
        )}
      </div>

      {/* Alerts Table */}
      {loading ? (
        <p>Loading alerts...</p>
      ) : (
        <table className="alerts-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Country</th>
              <th>Title</th>
              <th>Description</th>
              <th>Link</th>
            </tr>
          </thead>
          <tbody>
            {filteredAlerts.length > 0 ? (
              filteredAlerts.map((alert, index) => (
                <tr key={index}>
                  <td>{alert.date}</td>
                  <td>{alert.country}</td>
                  <td>{alert.title}</td>
                  <td>{alert.description}</td>
                  <td>
                    <a href={alert.link} target="_blank" rel="noopener noreferrer">
                      More Info
                    </a>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="no-alerts">
                  No alerts found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AlertsReports;
