import React, { useState } from 'react';
import './MyArticles.css';

const MyArticles = () => {
  const [articles, setArticles] = useState([
    {
      id: 1,
      title: 'Healthy Eating Habits and its Importance',
      content: 'This article discusses the importance of healthy eating habits. A well-balanced diet plays a crucial role in maintaining overall health and preventing chronic diseases. Proper nutrition ensures that the body receives essential vitamins, minerals, and other nutrients necessary for growth, energy production, and the maintenance of healthy body functions.',
      datePosted: '2024-10-01',
      comments: 5,
      expanded: false
    },
    {
      id: 2,
      title: 'Benefits of Regular Exercise',
      content: 'Exercising regularly has a number of benefits for your body. Engaging in physical activities improves cardiovascular health, strengthens muscles, enhances flexibility, and helps maintain a healthy weight. Incorporating physical activity into your routine doesn’t require intense training sessions; even moderate activities like walking, cycling, or swimming can offer significant health benefits.',
      datePosted: '2024-10-05',
      comments: 3,
      expanded: false
    },
    {
      id: 3,
      title: 'Stress Management Techniques',
      content: 'Managing stress effectively can improve both mental and physical health. Chronic stress can lead to a range of health problems, including high blood pressure, heart disease, obesity, and diabetes. Learning to manage stress through techniques like meditation, deep breathing exercises, and mindfulness can significantly improve one’s well-being. Other effective methods for managing stress include regular physical activity, engaging in hobbies, and maintaining social connections. A balanced approach to work and life, combined with healthy coping mechanisms, can help mitigate the harmful effects of stress and promote a sense of peace and stability. Implementing these techniques can lead to better emotional resilience, improved relationships, and a more positive outlook on life.',
      datePosted: '2024-10-12',
      comments: 7,
      expanded: false
    }
  ]);

  const handleExpand = (id) => {
    setArticles(articles.map(article => 
      article.id === id ? { ...article, expanded: !article.expanded } : article
    ));
  };

  return (
    <div className="articles-container">
      <h2>My Articles</h2>
      <div className="articles-row">
        {articles.map((article) => (
          <div key={article.id} className="article-item">
            <div className="article-thumbnail" onClick={() => handleExpand(article.id)}>
              <h3>{article.title}</h3>
              <p className="article-date">Posted on: {article.datePosted}</p>
              <p className="article-comments">Comments: {article.comments}</p>
              <button className="expand-btn">{article.expanded ? 'Collapse' : 'Read More'}</button>
            </div>
            {article.expanded && (
              <div className="article-content">
                <p>{article.content}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyArticles;
