import React, { useState } from 'react';
import './Category.css';

const Category = () => {
  const categories = {
    freeWorkoutPlans: [
      { imgSrc: '/free1.jpg', title: 'Free Plan 1', description: 'Beginner-friendly workout plan.' },
      { imgSrc: '/free2.jpg', title: 'Free Plan 2', description: 'Intermediate level plan for strength.' },
      { imgSrc: '/free3.jpg', title: 'Free Plan 3', description: 'Cardio and endurance training.' },
      { imgSrc: '/free4.jpg', title: 'Free Plan 4', description: 'Stretching and flexibility exercises.' },
      { imgSrc: '/free5.jpg', title: 'Free Plan 5', description: 'HIIT workouts for fat loss.' },
      { imgSrc: '/free6.jpg', title: 'Free Plan 6', description: 'Strength and conditioning.' },
      { imgSrc: '/free7.jpg', title: 'Free Plan 7', description: 'Core strengthening routines.' },
      { imgSrc: '/free8.jpg', title: 'Free Plan 8', description: 'Full body workout plan.' },
    ],
    // ... (other categories as in your original code)
  };

  // Helper function to render a set of items (4 items per row)
  const renderCategoryItems = (items) => {
    return items.map((item, index) => (
      <div className="category-item" key={index}>
        <img src={item.imgSrc} alt={item.title} />
        <h3>{item.title}</h3>
        <p>{item.description}</p>
      </div>
    ));
  };

  // Generic Carousel Component for Each Category
  const CategoryCarousel = ({ title, category }) => {
    const [startIndex, setStartIndex] = useState(0);

    const handleNext = () => {
      setStartIndex((prevIndex) => 
        (prevIndex + 4) % category.length
      );
    };

    const handlePrev = () => {
      setStartIndex((prevIndex) => 
        (prevIndex - 4 + category.length) % category.length
      );
    };

    return (
      <div className="category-section">
        <h3 className="category-title">{title}</h3>
        <div className="carousel-wrapper">
          <button className="nav-arrow left-arrow" onClick={handlePrev}>
            &#9664; {/* Left Arrow */}
          </button>
          <div className="category-row">
            {renderCategoryItems(category.slice(startIndex, startIndex + 4))}
          </div>
          <button className="nav-arrow right-arrow" onClick={handleNext}>
            &#9654; {/* Right Arrow */}
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="categories-container">
      <h2 className="section-title">Categories</h2>

      <CategoryCarousel title="Free Workout Plans" category={categories.freeWorkoutPlans} />
      <CategoryCarousel title="Paid Workout Plans" category={categories.paidWorkoutPlans} />
      <CategoryCarousel title="Workout with Nutrition Guidance" category={categories.workoutWithNutrition} />
      <CategoryCarousel title="Customize Your Workout" category={categories.customizeWorkout} />
      <CategoryCarousel title="Diet Plans" category={categories.dietPlans} />
      <CategoryCarousel title="Personal Coach" category={categories.personalCoach} />
    </div>
  );
};

export default Category;
