import React, { useState, useEffect } from "react";

function AI() {
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost/rag2/api/analyze.php?url=https://www.ndtv.com/topic/maharashtra-protest")
      .then((response) => response.json())
      .then((data) => {
        setAnalysis(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching analysis:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="analysis-container">
      <h1>AI News Analysis</h1>

      {loading ? <p>Analyzing news...</p> : analysis ? (
        <div className="analysis-results">
          <h2>Analysis Results</h2>
          <p><strong>Title:</strong> {analysis.title}</p>
          <p><strong>Authenticity Score:</strong> {analysis.authenticity_score}%</p>
          <p><strong>Sentiment:</strong> {analysis.sentiment}</p>
          <p><strong>Summary:</strong> {analysis.summary}</p>
          <p><strong>Source:</strong> <a href={analysis.url} target="_blank" rel="noopener noreferrer">{analysis.url}</a></p>
        </div>
      ) : (
        <p>Failed to load analysis.</p>
      )}
    </div>
  );
}y

export default ArI;
