import React, { useState } from "react";
import "./User.css";

const User = ({ currentUserRole }) => {
  const [admins, setAdmins] = useState([
    { id: 1, name: "John Doe", role: "Super Admin", lastLogin: "2025-03-29", status: "Active" },
    { id: 2, name: "Jane Smith", role: "Admin", lastLogin: "2025-03-27", status: "Disabled" },
    { id: 3, name: "David Johnson", role: "Editor", lastLogin: "2025-03-25", status: "Active" },
  ]);

  const [newAdmin, setNewAdmin] = useState({ name: "", role: "Admin", status: "Active" });

  // Role-Based Access Control
  const roleHierarchy = {
    "Super Admin": ["Super Admin", "Admin", "Editor", "Viewer"],
    "Admin": ["Admin", "Editor", "Viewer"],
    "Editor": ["Viewer"],
    "Viewer": [],
  };

  // Handle Input Change
  const handleInputChange = (e) => {
    setNewAdmin({ ...newAdmin, [e.target.name]: e.target.value });
  };

  // Add New Admin
  const addAdmin = () => {
    if (newAdmin.name.trim() === "" || !roleHierarchy[currentUserRole].includes(newAdmin.role)) return;
    setAdmins([...admins, { ...newAdmin, id: admins.length + 1, lastLogin: "Never" }]);
    setNewAdmin({ name: "", role: "Admin", status: "Active" });
  };

  // Edit Admin Role
  const editAdminRole = (id, newRole) => {
    if (!roleHierarchy[currentUserRole].includes(newRole)) return;
    setAdmins(admins.map(admin => (admin.id === id ? { ...admin, role: newRole } : admin)));
  };

  // Toggle Admin Status
  const toggleStatus = (id) => {
    setAdmins(admins.map(admin => 
      admin.id === id ? { ...admin, status: admin.status === "Active" ? "Disabled" : "Active" } : admin
    ));
  };

  // Delete Admin
  const deleteAdmin = (id) => {
    setAdmins(admins.filter(admin => admin.id !== id));
  };

  return (
    <div className="user-management-container">
      <h1>üë§ Admin User Management</h1>

      {/* Only Super Admin & Admin can add new admins */}
      {["Super Admin", "Admin"].includes(currentUserRole) && (
        <div className="add-admin">
          <input
            type="text"
            name="name"
            placeholder="Enter Admin Name"
            value={newAdmin.name}
            onChange={handleInputChange}
          />
          <select name="role" value={newAdmin.role} onChange={handleInputChange}>
            {roleHierarchy[currentUserRole].map((role) => (
              <option key={role} value={role}>{role}</option>
            ))}
          </select>
          <button onClick={addAdmin} className="add-button">‚ûï Add New Admin</button>
        </div>
      )}

      {/* Admins Table */}
      <table className="admin-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Last Login</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.map((admin) => (
            <tr key={admin.id}>
              <td>{admin.name}</td>
              <td>
                {/* Super Admin & Admin can edit roles below their level */}
                {["Super Admin", "Admin"].includes(currentUserRole) && roleHierarchy[currentUserRole].includes(admin.role) ? (
                  <select value={admin.role} onChange={(e) => editAdminRole(admin.id, e.target.value)}>
                    {roleHierarchy[currentUserRole].map((role) => (
                      <option key={role} value={role}>{role}</option>
                    ))}
                  </select>
                ) : (
                  admin.role
                )}
              </td>
              <td>{admin.lastLogin}</td>
              <td className={admin.status === "Active" ? "status-active" : "status-disabled"}>
                {admin.status}
              </td>
              <td>
                {/* Only Super Admin & Admin can enable/disable users */}
                {["Super Admin", "Admin"].includes(currentUserRole) && roleHierarchy[currentUserRole].includes(admin.role) && (
                  <button className="status-btn" onClick={() => toggleStatus(admin.id)}>
                    {admin.status === "Active" ? "üî¥ Disable" : "üü¢ Enable"}
                  </button>
                )}
                {/* Super Admin can delete any user, Admin can delete Editor & Viewer */}
                {currentUserRole === "Super Admin" || (currentUserRole === "Admin" && ["Editor", "Viewer"].includes(admin.role)) ? (
                  <button className="delete-btn" onClick={() => deleteAdmin(admin.id)}>‚ùå Delete</button>
                ) : null}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default User;
