import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Profile from './components/Profile';
import Register from './components/Register';
import Login from './components/Login';
import Stories from './components/Stories';
import Footer from './components/Footer';
import Dashboard from "./components/Dashboard";
import DataCollection from "./components/DataCollection";
import ArI from "./components/ArI"; // Updated Import
import AlertsReports from './components/AlertsReports';
import User from './components/User';
import SystemSettings from './components/SystemSettings';


function App() {
  const [showRegister, setShowRegister] = useState(false);
  const [showSettings, setShowSettings] = useState(false); // New state for System Settings

  // Simulating a logged-in user role (Replace with actual authentication logic)
  const currentUserRole = "Admin"; // Change this to test: "Super Admin", "Editor", "Viewer"

  const toggleRegister = () => {
    setShowRegister(!showRegister);
  };

  return (
    <Router>
      {/* Navbar with Profile click triggering System Settings */}
      <Navbar 
        onSignUpClick={toggleRegister} 
        onProfileClick={() => setShowSettings(true)} // Open settings when Profile is clicked
      />

      {/* Show Register Modal */}
      {showRegister && <Register onClose={toggleRegister} />}

      {/* Show System Settings Overlay */}
      {showSettings && <SystemSettings onClose={() => setShowSettings(false)} />}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/ai-analysis" element={<ArI />} />  {/* Updated Path */}
        <Route path="/stories" element={<Stories />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/data-collection" element={<DataCollection />} />
        <Route path="/AlertsReports" element={<AlertsReports />} />

        {/* Passing currentUserRole to User Component */}
        <Route path="/user" element={<User currentUserRole={currentUserRole} />} />
      </Routes>

      <Footer />
    </Router>
  );
}

export default App;
