import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const [selectedCountry, setSelectedCountry] = useState("");
  const [activePanel, setActivePanel] = useState("Live News Feed");
  const [news, setNews] = useState([]);

  const API_KEY = "pub_788677aa36735d8282ee66b9146090ddf3d0f";

  const countryList = [
    { name: "India", code: "IN" },
    { name: "United States", code: "US" },
    { name: "United Kingdom", code: "GB" },
    { name: "Australia", code: "AU" },
    { name: "Germany", code: "DE" },
    { name: "Canada", code: "CA" },
    { name: "France", code: "FR" },
    { name: "Japan", code: "JP" },
    { name: "China", code: "CN" },
    { name: "Russia", code: "RU" }
  ];

  // Convert country name to country code
  const getCountryCode = (name) => {
    const found = countryList.find(c => c.name === name);
    return found ? found.code.toLowerCase() : "";
  };

  // Fetch news using selected country
  useEffect(() => {
    const fetchNews = async () => {
      if (selectedCountry && activePanel === "Live News Feed") {
        const countryCode = getCountryCode(selectedCountry);

        try {
          const response = await fetch(
            `https://newsdata.io/api/1/news?apikey=${API_KEY}&country=${countryCode}&language=en`
          );
          const data = await response.json();
          setNews(data.results || []);
        } catch (err) {
          console.error("Failed to fetch news:", err);
          setNews([]);
        }
      }
    };

    fetchNews();
  }, [selectedCountry, activePanel]);

  return (
    <div className="dashboard">

      {/* Dropdown */}
      <div className="dropdown-container">
        <label htmlFor="country">Select a Country:</label>
        <select
          id="country"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">-- Choose a Country --</option>
          {countryList.map((country, index) => (
            <option key={index} value={country.name}>{country.name}</option>
          ))}
        </select>
      </div>

      {/* Panel Buttons */}
      <div className="panel-buttons">
        {["Live News Feed", "AI-Analysis Summary", "Recent Alerts Panel"].map((panel) => (
          <button
            key={panel}
            className={activePanel === panel ? "active" : ""}
            onClick={() => setActivePanel(panel)}
          >
            {panel}
          </button>
        ))}
      </div>

      {/* Panel Content */}
      <div className="panel-content">
        {activePanel === "Live News Feed" && (
          <div className="news-feed">
            {news.length > 0 ? (
              news.slice(0, 6).map((article, idx) => (
                <div className="news-card" key={idx}>
                  <h3>{article.title}</h3>
                  <p>{article.description?.slice(0, 100)}...</p>
                  <a href={article.link} target="_blank" rel="noopener noreferrer">
                    Read More →
                  </a>
                </div>
              ))
            ) : (
              <p>No news found. Please select a country.</p>
            )}
          </div>
        )}

        {activePanel === "AI-Analysis Summary" && (
          <div className="ai-panel">
            <h2>🧠 AI-Based Analysis</h2>
            <p>This section will provide summarized insights soon.</p>
          </div>
        )}

        {activePanel === "Recent Alerts Panel" && (
          <div className="alert-panel">
            <h2>🚨 Recent Alerts</h2>
            <p>This will display real-time alerts for the selected country.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
